mapper.select:
select b_id, b_pwd, b_name, b_address, b_email, b_email2, b_phone, b_profile, to_char(b_regdate,'YYYY-MM-DD') b_regdate, grade
from b_user
where b_id = #{b_id}

Select b_id
from b_user
where b_id=#{b_id}

Select b_id
from b_user
where b_name=#{b_name} and b_phone=#{b_phone}

Select b_pwd
from b_user
where b_id=#{b_id} and b_phone=#{b_phone}

mapper.update:
Update b_user
Set b_pwd = #{b_newPwd}
where b_id=#{b_id}

mapper.select:
SELECT NVL(MAX(ROWNUM),0)
FROM takeout
WHERE b_id=#{b_id,jdbcType=VARCHAR}

SELECT NVL(MAX(ROWNUM),0)
FROM reservation
WHERE b_id=#{b_id,jdbcType=VARCHAR}
